import { __decorate } from "tslib";
import { Component } from '@angular/core';
let NewMomentsComponent = class NewMomentsComponent {
    constructor(momentService, router) {
        this.momentService = momentService;
        this.router = router;
        this.btnText = 'Compartilhar';
    }
    ngOnInit() {
    }
    async createHandler(moment) {
        const formData = new FormData();
        formData.append('title', moment.title);
        formData.append('description', moment.description);
        if (moment.image) {
            formData.append('image', moment.image);
        }
        // todo
        await this.momentService.createMoment(formData).subscribe();
        this.messageService.addMessage('Momento adicionado com sucesso!');
        this.router.navigate(['/']);
    }
};
NewMomentsComponent = __decorate([
    Component({
        selector: 'app-new-moments',
        templateUrl: './new-moments.component.html',
        styleUrls: ['./new-moments.component.css']
    })
], NewMomentsComponent);
export { NewMomentsComponent };
